mapper.insert:
INSERT INTO CPES_PROGRAM
(
	PROGRAM_SEQ
  , PROGRAM_ID
  , PROGRAM_NM_KH
  , PROGRAM_NM_EN
  , PROGRAM_URL
  , RANGE_CD
  , USE_YN
  , REG_USER_SEQ
  , REG_DT
  , MOD_USER_SEQ
  , MOD_DT
)
VALUES
(
    FN_GET_UUID_SEQ()
  , #{programId}
  , #{programNmKh}
  , #{programNmEn}
  , #{programUrl}
  , #{rangeCd}
  , #{useYn}
  , #{regUserSeq}
  , now()
  , #{modUserSeq}
  , now()
)

mapper.update:
UPDATE CPES_PROGRAM
   SET  PROGRAM_NM_KH		= #{programNmKh}
	  , PROGRAM_NM_EN		= #{programNmEn}
	  , PROGRAM_URL			= #{programUrl}
	  , RANGE_CD			= #{rangeCd}
	  , USE_YN				= #{useYn}
      , MOD_USER_SEQ		= #{modUserSeq}
      , MOD_DT				= now()
WHERE   PROGRAM_SEQ			= #{programSeq}
